package Lesson_5_Expressions;

public class expressions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//expressions
		
		int x = 10;
		
		x = x + 1;
		
		System.out.println(x);
				
		x = x - 1;
		
		System.out.println(x);
		
		//modulus = remainder
		x = x % 3;
		
		System.out.println(x);
		
		//adds 1 to x
		x++;
		
		System.out.println(x);
		
		//subtracts 1 to x
		x--;
		
		System.out.println(x);
		
		//divides x by 3, however as its an int it will result in 3 instead of 3.3333
		x = x/3;
		
		System.out.println(x);
		
	}

}
