
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//println prints a new line after message
		System.out.println("First lesson!");
		
		//print prints on same line
		System.out.print("First lesson!");
		
		//can also use \n to skip to next line
		System.out.println("\n\nHello");
		
		//add a tab using \t
		System.out.println("\tHello");
	}

}
